import React, { ReactElement } from 'react';
import products from '../assets/mock/products';
import Link from 'next/link';
import '../styles/_products.scss';

interface Props {

}

export default function Products({}: Props): ReactElement {
  return (
    <div className="container">
    { products.map((product) => (
      <div className="product-container" key={product.id}>
        <figure>
          <Link href="/">
            <img src={`../assets/images/${product.image}`} alt={product.description} />
          </Link>
        </figure>
        <p>
        {product.name}
        </p>
        <p>
        {product.description}
        </p>
      </div>
    ))

    }
  </div>
  )
}
