import { ApolloClient, InMemoryCache} from '@apollo/client';

export const client = new ApolloClient({
    uri: 'http://localhost:5000/graphql',
    cache: new InMemoryCache({
        typePolicies: {
            User: {
                fields: {
                    roles: {
                        merge(_ignored, incoming){
                            return incoming
                        },
                    },
                },
            },
            Query: {
                fields: {
                    users: {
                        merge(_ignored, incoming){
                            return incoming
                        },
                    },
                },
            },
        },
    }),
    credentials:'include',
})
